package ezen;

public class Gugudan {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("구구단 연습문제");
		
		int i,j; // i=단
		for(i=2;i<=2;i++) {
				
			for(j=1;j<=9;j++) {
				System.out.println(i+"X"+j+"="+(i*j));
			}//안쪽 for끝
			System.out.println("");//구분용 빈줄
		}//바깥 for끝
		
	}

}
